            break;
        }
        case "B": {
            returnValue = "PB";
            break;
        }
        case "C": {
            returnValue = "BP";
            break;
        }
        case "D": {
            returnValue = "BP";
            break;
        }
        default: {
            returnValue = "B";
            break;
        }
    }

    return returnValue;
}
 
